/**
 * Ajaxifier class.
 */
var Ajaxifier = Class.create({
    /**
     * Initialize the ajaxifier.
     */
    initialize: function () {
        this.blocks = '';
        this.handles = '';
    },

    /**
     * Set the handles used by the ajaxifier.
     */
    setHandles: function (handles) {
        this.handles = handles;
    },

    /**
     * Add a block to the ajaxifier.
     */
    addBlock: function (blockId) {
        if (this.blocks) {
            this.blocks += ',';
        }
        this.blocks += blockId;
    },

    /**
     * Send the Ajax request.
     */
    launchRequest: function (url) {
        if (this.blocks && this.handles) {
            new Ajax.Request(url, {
                method: 'get',
                parameters: this.getParameters(),
                onSuccess: function (resp) {
                    for (var blockname in resp.responseJSON) {
                        $(blockname).replace(resp.responseJSON[blockname]);
                    }
                }
            });
        }
    },

    /**
     * Get the parameters to use for the Ajax request.
     */
    getParameters: function () {
        return 'no_cache&handles=' + this.handles + '&blocks=' + this.blocks;
    },

    /**
     * Get the URL to call for the Ajax request.
     */
    getCallUrl: function () {
        var baseUrl = window.location.protocol + '//' + window.location.host;
        return baseUrl + '/ajaxifier/call/index/';
    }
});

var ajaxifier = new Ajaxifier;


