function getColissimoRelaiDetails(id,name){
        var content = '<ul>';
        content += '<li><strong>'+name+'</strong></li>';
        jQuery.ajax({
    	  type: 'GET', // Le type de ma requete
    	  async: false, 
    	  url: '/smilesocolissimo/relais/gethoraires/id/' + id, // L'url vers laquelle la requete sera envoyee
    	  success: function(data, textStatus, jqXHR) {
    		  content += data;
    	  },
    	  error: function(jqXHR, textStatus, errorThrown) {
    	    // Une erreur s'est produite lors de la requete
    	  }
    	});
        addInfoPopup(content); // open same popup as TNT
}
