//SETTING UP OUR POPUP
//0 means disabled; 1 means enabled;
var popupStatus = 0;

//loading popup with $j magic!
function loadPopup() {
    //loads popup only if it is disabled
    if (popupStatus == 0) {
        jQuery("#popup-gift").fadeIn();
        jQuery('.overlay-cart').removeClass('hide');
        //jQuery("#backgroundPopup").fadeIn();
        popupStatus = 1;
    }
}

//disabling popup with jQuery magic!
function disablePopup() {
    //disables popup only if it is enabled
    if (popupStatus == 1) {
//		jQuery("#backgroundPopup").fadeOut("slow");
        jQuery("#popup-gift").fadeOut("slow");
        jQuery('.overlay-cart').addClass('hide');
        popupStatus = 0;
    }
}

//CONTROLLING EVENTS IN jQuery
jQuery(document).ready(function () {

    //LOADING POPUP
    //Click the button event!
    jQuery("#button").click(function (event) {
        event.preventDefault();
        //load popup
        loadPopup();
    });

    //CLOSING POPUP
    //Click the x event!
    jQuery("#popup-gift-close").click(function () {
        disablePopup();
    });
    //Click out event!
    jQuery("#backgroundPopup").click(function () {
        disablePopup();
    });
    //Press Escape event!
    jQuery(document).keypress(function (e) {
        if (e.keyCode == 27 && popupStatus == 1) {
            disablePopup();
        }
    });

    jQuery('.overlay-cart').click(
        function () {
            disablePopup();
        }
    );

});
